var tipos=[30,50,100],
    potencia=[1.5,2,3,4,5,8,12.5,15,20], // Potência Recomendada
    minimo=[160,215,320,430,525,839,1311,1574,2140], // Mínimo do Kit
    maximo=[215,285,430,570,700,1120,1750,2100,2855], // Máximo do Kit
	telha_metalica=['15.985,30','19.353,76','26.041,52','32.149,55','37.662,59','59.089,88','85.359,77','102.467,35','131.991,2'], // Preços da Telha Metálica
    telha_ceramica=['16.829,05','20.478,72','27.728,96','34.399,47','40.474,99','63.589,72','92.109,53','110.904,55','143.240,8']; // Preços da Telha Cerâmica

function calc(consumo = 0, tipo = 1)
{
		for(var e=0; consumo-tipos[tipo-1]>maximo[e];) e++;
		return [consumo-tipos[tipo-1], potencia[e], telha_metalica[e], telha_ceramica[e]];
}

function clique() {
	    var consumo = jQuery("#consumo").val();
		if( consumo == "" ) {
			alert( 'Insira um Consumo' );
		} else {
			var vet = calc(jQuery("#consumo").val(),jQuery("#tipo").val());
			jQuery("span#kit-recomendado").text(vet[1]), jQuery("span#telha_metalica").text(vet[2]), jQuery("span#telha_metalica").addClass("money"), jQuery("span#telha_ceramica").text(vet[3]), jQuery("span#telha_ceramica").addClass("money");

			//appears box
			jQuery("#result, .overlay").fadeIn();
		}
	
		//disappears box
		jQuery(".overlay").click(function () {
		   jQuery("#result, .overlay").fadeOut();
		   jQuery(".result").fadeIn('slow');
		   jQuery(".quote").fadeOut('slow');
		});
	
}	

//change box
function quote() {
	jQuery('.quote').fadeIn('slow');
	jQuery('.result').fadeOut('fast');
}